import React, { useState } from "react";
import "./ExchangeForm.scss";
import { exchangeService } from "../_services";
import { exchangeData } from "../types";

interface ExchangeFormProps {
  data?: exchangeData;
  formType: "add" | "edit";
}

export const ExchangeForm: React.FC<ExchangeFormProps> = ({
  data,
  formType,
}) => {
  const [apiKey, setApiKey] = useState(data ? data.apiKey : "");
  const [apiSecret, setApiSecret] = useState(data ? data.apiSecret : "");
  const [passphrase, setPassphrase] = useState(data ? data.passphrase : "");
  const [name, setName] = useState(data ? data.name : "");
  const [nickname, setNickname] = useState(data ? data.nickname : "");

  const { addNew, update } = exchangeService;

  const handleSubmit = async () => {
    const exchange: exchangeData = {
      apiKey,
      apiSecret,
      passphrase,
      name,
      nickname,
      imageURL: "data:image/png;base64,"
    };
    const response = await addNew(exchange);
    console.log(response);
  };

  const handleUpdate = async () => {
    const exchange: exchangeData = {
      apiKey,
      apiSecret,
      passphrase,
      name,
      nickname,
      imageURL: data.imageURL,
    };
    const response = await addNew(exchange);
  };

  const formTypeEdit = {
    formTitle: "Edit Exchange",
    buttonText: "Update",
    buttonAction: handleUpdate,
  };
  const formTypeAdd = {
    formTitle: "Add Exchange",
    buttonText: "Submit",
    buttonAction: handleSubmit,
  };
  const formTypeData = formType == "edit" ? formTypeEdit : formTypeAdd;

  return (

    <form className="add-exchange-form">
      <div>
        <h1>{formTypeData.formTitle}</h1>
      </div>
      <div className="add-exchange-data-row">
        <label className="add-exchange-row-label">Exchange Name</label>
        <input
          name="name"
          onChange={(e) => setName(e.target.value)}
          type="text"
          value={name}
          className="add-exchange-row-input"
        />
      </div>
      <div className="add-exchange-data-row">
        <label className="add-exchange-row-label">Nickname</label>
        <input
          name="nickname"
          onChange={(e) => setNickname(e.target.value)}
          type="text"
          value={nickname}
          className="add-exchange-row-input"
        />
      </div>
      <div className="add-exchange-data-row">
        <label className="add-exchange-row-label">Api Key</label>
        <input
          name="apiKey"
          onChange={(e) => setApiKey(e.target.value)}
          type="text"
          value={apiKey}
          className="add-exchange-row-input"
        />
      </div>
      <div className="add-exchange-data-row">
        <label className="add-exchange-row-label">Api secret</label>
        <input
          name="apiSecret"
          onChange={(e) => setApiSecret(e.target.value)}
          type="text"
          value={apiSecret}
          className="add-exchange-row-input"
        />
      </div>
      <div className="add-exchange-data-row">
        <label className="add-exchange-row-label">Passphrase</label>
        <input
          name="passphrase"
          onChange={(e) => setPassphrase(e.target.value)}
          type="text"
          value={passphrase}
          className="add-exchange-row-input"
        />
      </div>
      <div>
        <div
          onClick={() => formTypeData.buttonAction()}
          className="center-my-children"
          style={{
            borderRadius: "3px",
            border: "1px solid black",
            height: "40px",
            backgroundColor: "green",
            color: "white",
          }}
        >
          <div style={{cursor: "pointer"}}>{formTypeData.buttonText}</div>
        </div>
      </div>
    </form>
  );
};
